import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { UserAuthProvider } from './components/Auth/AuthenticationContext'; // Updated
import UserLogin from './components/Auth/UserLogin'; // No change
import UserRegistration from './components/Auth/UserRegistration'; // No change
import UserDashboard from './components/Dashboard/UserDashboard'; // No change
import BudgetOverview from './components/Dashboard/BudgetOverview'; // Renamed from BudgetList
import FinancialChart from './components/Dashboard/FinancialChart'; // Renamed from BudgetChart
import AddBudgetCapacity from './components/Dashboard/BudgetCapacityManager'; // No change
import CreateBudget from './components/Dashboard/CreateBudget'; // Renamed from AddBudget
import authService from './components/services/authService'; // No change

import './styles/style.css'; // No change


const HomePage = () => {
  return (
    <div className="home-container">
      <h1 className="welcome-message">Welcome to the Budget App</h1>
      <h2>Your personal budget management solution.</h2>
      <div className="home-button-container">
        <Link to="/login" className="home-button">
          Log In
        </Link>
        <Link to="/signup" className="home-button">
          Sign Up
        </Link>
      </div>
    </div>
  );
};

const App = () => {
  const [isUserLoggedIn, setIsUserLoggedIn] = useState(false);
  const [token, setUserToken] = useState(null);
  const [isTokenRefreshModalOpen, setIsTokenRefreshModalOpen] = useState(false);

  const handleUserLogin = (token) => {
    setUserToken(token);
    setIsUserLoggedIn(true);
  };

  useEffect(() => {
    const checkUserTokenExpiry = async () => {
      if (authService.checkUserTokenExpiry()) {
        setIsTokenRefreshModalOpen(true);
      }
    };

    checkUserTokenExpiry();
  }, []);

  const handleRefreshUserToken = async () => {
    try {
      await authService.refreshUserAccessToken();
      setIsTokenRefreshModalOpen(false);
    } catch (error) {
      console.error('Error refreshing user user token:', error);
    }
  };

  return (
    <Router>
      <UserAuthProvider>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<UserLogin onUserLogin={handleUserLogin} />} />
          <Route path="/signup" element={<UserRegistration />} />
          <Route
            path="/dashboard"
            element={isUserLoggedIn ? <UserDashboard token={token} /> : <Navigate to="/login" />}
          />
          {isUserLoggedIn && (
            <>
              <Route path="/dashboard/budget-list" element={<BudgetOverview />} />
              <Route path="/dashboard/budget-chart" element={<FinancialChart />} />
              <Route path="/dashboard/configure-budget" element={<AddBudgetCapacity />} />
              <Route path="/dashboard/add-budget" element={<CreateBudget token={token} />} />
            </>
          )}
        </Routes>
      </UserAuthProvider>
    </Router>
  );
};

export default App;
