//Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Modal from 'react-modal';
import authService from '../services/authService';
import '../../styles/login.css';

const UserLogin = ({ onUserLogin }) => {
  const [userUsername, setUserUsername] = useState('');
  const [userPassword, setUserPassword] = useState('');
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [loginStatusMessage, setLoginStatusMessage] = useState('');
  const navigate = useNavigate();

  const handleUserLogin = async () => {
    try {
      const token = await authService.userLogin(userUsername, userPassword);

      // Pass the user token and username to the onUserLogin callback
      onUserLogin(token, userUsername);

      setLoginStatusMessage('Login successful');
      openLoginModal();
      navigate('/dashboard');
    } catch (error) {
      console.error('User login failed', error);
      setLoginStatusMessage('Login failed. Please check your username and password.');
      openLoginModal();
    }
  };

  const openLoginModal = () => {
    setIsLoginModalOpen(true);
  };

  const closeLoginModal = () => {
    setIsLoginModalOpen(false);
  };

  return (
    <div
      className="login-background"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/background_homepage.png'})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        height: '100vh', // Set to the height of the viewport
      }}
    >
      {/* Your login form and other components here */}
      
      {/* Modal for displaying login status message */}
      <Modal
        isOpen={isLoginModalOpen}
        onRequestClose={closeLoginModal}
        contentLabel="Login Status Message"
        className="login-modal"
        style={{
          overlay: {
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          },
        }}
      >
        <div className="modal-content">
          <h2>{loginStatusMessage}</h2>
          <button className="login-button" onClick={closeLoginModal}>
            Close
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default UserLogin;
