import React, { useState } from 'react';
import axios from 'axios';
import '../../styles/AddBudget.css';
import config from '../../config';

const BASE_URL = config.apiUrl;

const AddBudget = ({ token }) => {
  const [category, setCategory] = useState('');
  const [budgetAmount, setBudgetAmount] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [notification, setNotification] = useState(null);

  const handleAddBudget = async () => {
    try {
      const response = await axios.post(
        BASE_URL + '/api/budgets',
        { budgetName: category, budgetNumber: budgetAmount, selectedDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log(response.data);

      setNotification({ type: 'success', message: 'Budget added successfully' });

      setCategory('');
      setBudgetAmount('');
      setSelectedDate(new Date());
    } catch (error) {
      const errorMessage = error.response?.data?.message || 'An error occurred';

      setNotification({ type: 'error', message: `Error: ${errorMessage}` });
    }
  };

  return (
    <div className="add-budget-form">
      <h2>Add a New Budget</h2>
      <div className="form-group">
        <label>Date:</label>
        <DatePicker selected={selectedDate} onChange={(date) => setSelectedDate(date)} />
      </div>
      <div className="form-group">
        <label>Category:</label>
        <input type="text" placeholder="Enter budget category" value={category} onChange={(e) => setCategory(e.target.value)} />
      </div>
      <div className="form-group">
        <label>Amount:</label>
        <input
          type="number"
          placeholder="Enter budget amount"
          value={budgetAmount}
          onChange={(e) => setBudgetAmount(e.target.value)}
        />
      </div>
      <button className="add-button" onClick={handleAddBudget}>
        Add Budget
      </button>

      {notification && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}
    </div>
  );
};

export default AddBudget;
