import React, { useState, useEffect } from 'react';
import authService from '../services/authService';
import '../../styles/signup.css';

const UserRegistration = () => {
  const [userUsername, setUserUsername] = useState('');
  const [userPassword, setUserPassword] = useState('');
  const [userFullName, setUserFullName] = useState('');
  const [registrationStatus, setRegistrationStatus] = useState(null);

  useEffect(() => {
    const closeDialog = () => {
      setTimeout(() => setRegistrationStatus(null), 2000);
    };

    if (registrationStatus === 'success' || registrationStatus === 'failed') {
      closeDialog();
    }
  }, [registrationStatus]);

  const handleUserRegistration = async (e) => {
    e.preventDefault();

    try {
      await authService.userRegistration(userUsername, userPassword, userFullName);
      setRegistrationStatus('success');
    } catch (error) {
      console.error('User registration failed', error);
      if (error.response && error.response.data && error.response.data.message) {
        // Display the server's error message
        setRegistrationStatus(`failed: ${error.response.data.message}`);
      } else {
        setRegistrationStatus('failed');
      }
    }
  };

  return (
    <div className="signup-container">
      <h2 className="signup-heading">Sign Up</h2>
      <form className="signup-form" onSubmit={handleUserRegistration}>
        <label className="signup-label">
          Full Name:
          <input
            className="signup-input"
            type="text"
            value={userFullName}
            onChange={(e) => setUserFullName(e.target.value)}
            aria-label="Full Name:"
          />
        </label>
        <label className="signup-label">
          Username:
          <input
            className="signup-input"
            type="text"
            value={userUsername}
            onChange={(e) => setUserUsername(e.target.value)}
            aria-label="Username:"
          />
        </label>
        <label className="signup-label">
          Password:
          <input
            className="signup-input"
            type="password"
            value={userPassword}
            onChange={(e) => setUserPassword(e.target.value)}
            aria-label="Password:"
          />
        </label>
        <button className="signup-button" type="submit">
          Sign Up
        </button>

        {registrationStatus === 'success' && (
          <div className="dialog success">
            Signup successful!
          </div>
        )}
        {registrationStatus === 'failed' && (
          <div className="dialog error">User registration failed. Please try again.</div>
        )}
      </form>
    </div>
  );
};

export default UserRegistration;
